    CombinacionesPage.prototype.AdicionCombosToPreview = function () {
        var _this = this;
        var loading = this.loadingCtrl.create({
            content: 'Cargando...'
        });
        loading.present();
        var token = window.localStorage.getItem("userToken");
        try {
            //console.log(Constantes.getPromocionalesUrl(token))
            this.httpRequest.get(__WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].getPromocionalesUrl(token)).then(function (res) {
                res = res.json();
                var data = res.COMBOS;
                if (data != null) {
                    //console.log(Object.keys(data));
                    var combo_actual = null;
                    var combosprevios = Object.keys(data).map(function (index) {
                        var promo = data[index];
                        return promo;
                    });
                    if (_this.weekdays[_this.currentDate.getDay()] == "martes" || _this.weekdays[_this.currentDate.getDay()] == "viernes") {
                        combo_actual = combosprevios[0];
                    }
                    if (_this.weekdays[_this.currentDate.getDay()] == "miercoles") {
                        combo_actual = combosprevios[1];
                    }
                    else if (_this.weekdays[_this.currentDate.getDay()] == "jueves") {
                        combo_actual = combosprevios[2];
                    }
                    else if (_this.weekdays[_this.currentDate.getDay()] == "sabado") {
                        combo_actual = combosprevios[3];
                    }
                    else if (_this.weekdays[_this.currentDate.getDay()] == "domingo") {
                        combo_actual = combosprevios[4];
                    }
                    _this.navCtrl.push(__WEBPACK_IMPORTED_MODULE_14__detalle_promocional_detalle_promocional__["a" /* DetallePromocionalPage */], {
                        objetivo: "nuevo-combo",
                        comboPrevio: combo_actual
                    });
                    // this.promociones = res.COMBOS;
                    // console.log(this.promociones);
                }
                else {
                    if (data.STATUS != 'OK') {
                        _this.mostrarMensaje(__WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].ALGO_ANDA_MAL, __WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].INTENTALO_NUEVAMENTE);
                    }
                }
                loading.dismiss();
            }, function (error) {
                console.log("Error");
                loading.dismiss();
                _this.mostrarMensaje(__WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].ALGO_ANDA_MAL, __WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].INTENTALO_NUEVAMENTE);
            });
        }
        catch (err) {
            this.mostrarMensaje(__WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].SIN_CONEXION, __WEBPACK_IMPORTED_MODULE_8__util_constantes__["a" /* Constantes */].REVISAR_CONEXION);
        }
    };
