    CombinacionesPage.prototype.eliminarElemento = function (elemento, tipo) {
        var _this = this;
        var alert = this.alertCtrl.create({
            title: 'Borrar elemento',
            message: '¿Estás seguro que deseas borrar este elemento de tu resumen? ',
            buttons: [
                {
                    text: 'No',
                    role: 'cancel',
                },
                {
                    text: 'Si',
                    handler: function () {
                        if (tipo == "PIZZA") {
                            _this.eliminarPizza(elemento);
                        }
                        else if (tipo == "ADICIONAL") {
                            _this.eliminarAdicional(elemento);
                        }
                        else if (tipo == "TRADICIONAL") {
                            _this.eliminarTradicional(elemento);
                        }
                        else if (tipo == "COMBO") {
                            _this.eliminarCombo(elemento);
                        }
                        else if (tipo == "CUPON") {
                            _this.eliminarCupon(elemento);
                        }
                    }
                }
            ]
        });
        alert.present();
    };
