    PizzaComboEspecialPage.prototype.escucharEventos = function () {
        var _this = this;
        /* captura de evento de seleccion de ingredientes cuando son agregados por primera vez o con un cambio de porcion */
        this.events.subscribe('seleccion-ingrediente', function (ingrediente) {
            var ingredienteIndex;
            if (ingrediente.checked == true) {
                if (_this.pizzaUno.ingredientes.length > 1) {
                    //this.pizzaUno.ingredientes[0].checked = false;
                    _this.presentAlert("Ingredientes excedidos", "Solo puede elegir hasta dos ingredientes");
                    return;
                }
                else {
                    if (_this.pizzaUno.ingredientes.filter(function (e, index) { return e.id === ingrediente.id; }).length > 0) { //si ya contiene el elemento
                        ingredienteIndex = _this.pizzaUno.ingredientes.map(function (x) { return x.id; }).indexOf(ingrediente.id);
                        _this.pizzaUno.ingredientes[ingredienteIndex] = ingrediente; //se reemplaza
                    }
                    else {
                        _this.pizzaUno.ingredientes.push(ingrediente); //se agrega
                    }
                }
                console.log(_this.pizzaUno.ingredientes);
            }
            else {
                _this.removeItemFromArr(_this.pizzaUno.ingredientes, ingrediente);
            }
        });
    };
    PizzaComboEspecialPage.prototype.terminarEventos = function () {
        this.events.unsubscribe('seleccion-ingrediente');
    };
