    PizzaComboEspecialDosPage.prototype.escucharEventos = function () {
        var _this = this;
        /* captura de evento de seleccion de ingredientes cuando son agregados por primera vez o con un cambio de porcion */
        this.events.subscribe('seleccion-ingrediente', function (ingrediente) {
            var ingredienteIndex;
            if (ingrediente.checked == true) {
                if (_this.pizzaDos.ingredientes.length > 1) {
                    //this.pizzaDos.ingredientes[0].checked = false;
                    _this.presentAlert("Ingredientes excedidos", "Solo puede elegir hasta dos ingredientes");
                    return;
                }
                else {
                    if (_this.pizzaDos.ingredientes.filter(function (e, index) { return e.id === ingrediente.id; }).length > 0) { //si ya contiene el elemento
                        ingredienteIndex = _this.pizzaDos.ingredientes.map(function (x) { return x.id; }).indexOf(ingrediente.id);
                        _this.pizzaDos.ingredientes[ingredienteIndex] = ingrediente; //se reemplaza
                    }
                    else {
                        _this.pizzaDos.ingredientes.push(ingrediente); //se agrega
                    }
                }
                console.log(_this.pizzaDos.ingredientes);
            }
            else {
                _this.removeItemFromArr(_this.pizzaDos.ingredientes, ingrediente);
            }
        });
    };
    PizzaComboEspecialDosPage.prototype.terminarEventos = function () {
        this.events.unsubscribe('seleccion-ingrediente');
    };
