        this.events.subscribe('seleccion-ingrediente', function (ingrediente) {
            var ingredienteIndex;
            if (ingrediente.checked == true) {
                if (_this.pizzaCuatro.ingredientes.length > 1) {
                    //this.pizzaCuatro.ingredientes[0].checked = false;
                    _this.presentAlert("Ingredientes excedidos", "Solo puede elegir hasta dos ingredientes");
                    return;
                }
                else {
                    if (_this.pizzaCuatro.ingredientes.filter(function (e, index) { return e.id === ingrediente.id; }).length > 0) { //si ya contiene el elemento
                        ingredienteIndex = _this.pizzaCuatro.ingredientes.map(function (x) { return x.id; }).indexOf(ingrediente.id);
                        _this.pizzaCuatro.ingredientes[ingredienteIndex] = ingrediente; //se reemplaza
                    }
                    else {
                        _this.pizzaCuatro.ingredientes.push(ingrediente); //se agrega
                    }
                }
                console.log(_this.pizzaCuatro.ingredientes);
            }
            else {
                _this.removeItemFromArr(_this.pizzaCuatro.ingredientes, ingrediente);
            }
        });
